;/*ееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееее*/typedef struct								/* Internal storage structure of path and seen-by */{	short				**nets;	short				**nodes;	short				items;} juncted;//	е Messages note: if area is undefined (i.e. area[0]==0) it is netmailtypedef struct {							/* Internal storage of message structure */	addr				from;				/* from address */	addr				to;					/* to address */	addr				reply;				/* reply address */							char				fromname[MName],						toname[MName];	char				date[MDate],						subj[MSubject];	long				flags;	long				flags_rsv;	long				base_flags;	char				**text;	char				area[256];	char				*msgid_str;	char				*reply_str;	long				msgid_crc;	long				reply_crc;	long				msgid_num;	long				reply_num;	long				storedtime;	juncted				seenby;	juncted				path;	long				msg_reply_prev;		//	prev. message in thread	long				msg_reply_next;		//	next message in thread} pktmsg;/*ееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееееее*/#endif