rd width, height		0							//	UserCon	} },	BeginSubs {},		ObjectData { ListBox {		1001, { 302, 202 }, visible, enabled,	// paneID, { width, height }		bound, bound, bound, bound,				// bound edges to superview		-1, -1, 0, defaultSuperView,			// left, top, userRefCon		hasHorizScroll, hasVertScroll, hasGrowBox, noFocusBox,		0, kGeneva10_Txtr,						// double-click msg, text traits		textList,								// LDEF ID		{ "One", "Two", "Three", "Four", "Five" }	// some sample data		} },	EndSubs {}} };// ===========================================================================//		• Window & Pane resources : Custom List// ===========================================================================resource 'WIND' (CustomList_WIND, purgeable) {	{ 47, 17, 247, 317 },	documentProc, 				// standard with size box & goAway	visible, goAway,	0x0,						// refCon	"Custom List",	noAutoCenter};resource 'PPob' (CustomList_PPob, purgeable) { {	ObjectData { Window {		CustomList_WIND, regular, hasCloseBox, hasTitleBar, hasResize, hasSizeBox, noZoom, hasShowNew,		enabled, hasTarget, hasGetSelectClick, noHideOnSuspend, noDelaySelect, hasEraseOnUpdate,		100, 100,					//	Minimum width, height		screenSize, screenSize,		//	Maximum width, height		screenSize, screenSize,		//	Standard width, height		0							//	UserCon	} },	BeginSubs {},		ClassAlias { 'mlst' },		ObjectData { ListBox {		1001, { 302, 202 }, visible, enabled,	// paneID, { width, height }		bound, bound, bound, bound,			// bound edges to superview		-1, -1, 0, defaultSuperView,		// left, top, userRefCon		hasHorizScroll, hasVertScroll, hasGrowBox, noFocusBox,		0, kGeneva10_Txtr,					// double-click msg, text traits		caller_LDEF,						// LDEF ID		{  }								// empty list		} },	EndSubs {}} };// ===========================================================================//		• Window & Pane resources : Hierarchical Demo List// ==================================================================