//	guimodule.c#include "guimodule.h"#include "guicode.h"static PyObject *ErrorObject;/* PyGUI methods */static voidPyGUI_dealloc(PyGUIObject *self){	PyObject_Del(self);}static PyMethodDef PyGUI_methods[] = {	/* sentinel */	{NULL,		NULL}};static PyObject *PyGUI_getattr(PyGUIObject *self, char *name){	return Py_FindMethod(PyGUI_methods, (PyObject *)self, name);}static intPyGUI_setattr(PyGUIObject *self, char *name, PyObject *v){	return -1;}PyTypeObject PyGUI_Type = {	/* The ob_type field must be initialized in the module init function	 * to be portable to Windows without using C++. */	PyObject_HEAD_INIT(NULL)	0,						/*ob_size*/	"PyGUI",				/*tp_name*/	sizeof(PyGUIObject),	/*tp_basicsize*/	0,						/*tp_itemsize*/	/* methods */	(destructor)PyGUI_dealloc,/*tp_dealloc*/	0,						/*tp_print*/	(getattrfunc)PyGUI_getattr, /*tp_getattr*/	(setattrfunc)PyGUI_setattr, /*tp_setattr*/	0,						/*tp_compare*/	0,						/*tp_repr*/	0,						/*tp_as_number*/	0,						/*tp_as_sequence*/	0,						/*tp_as_mapping*/	0,						/*tp_hash*/	0,                      /*tp_call*/	0,                      /*tp_str*/	0,                      /*tp_getattro*/	0,                      /*tp_setattro*/	0,                      /*tp_as_buffer*/	Py_TPFLAGS_DEFAULT,     /*tp_flags*/	0,                      /*tp_doc*/	0,                      /*tp_traverse*/	0,                      /*tp_clear*/	0,                      /*tp_richcompare*/	0,                      /*tp_weaklistoffset*/	0,                      /*tp_iter*/	0,                      /*tp_iternext*/	0,                      /*tp_methods*/	0,                      /*tp_members*/	0,                      /*tp_getset*/	0,                      /*tp_base*/	0,                      /*tp_dict*/	0,                      /*tp_descr_get*/	0,                      /*tp_descr_set*/	0,                      /*tp_dictoffset*/	0,                      /*tp_init*/	0,                      /*tp_alloc*/	0,                      /*tp_new*/	0,                      /*tp_free*/	0,                      /*tp_is_gc*/};/* --------------------------------------------------------------------- *//* Function of no arguments returning new PyGUI object */static PyObject *wrap_doSimpleListDlg(PyObject *self, PyObject *args){	long		res;	PyObject	*list;	if (!PyArg_ParseTuple(args,"O:doSimpleListDlg", &list))		return NULL;		if (!PyList_Check(list))		return NULL;		res = doSimpleListDlg(list);		return PyInt_FromLong(res);}/* List of functions defined in the module */static PyMethodDef PyGUI_functions[] = {	{"doSimpleListDlg",		(PyCFunction)wrap_doSimpleListDlg,		METH_VARARGS	},	/* sentinel */	{NULL,		NULL}};/* Initialization function for the module (*must* be called initxx) */DL_EXPORT(PyObject *)initPyGUI(void){	PyObject *m, *d;	/* Initialize the type of the new type object here; doing it here	 * is required for portability to Windows without requiring C++. */	PyGUI_Type.ob_type = &PyType_Type;	/* Create the module and add the functions */	m = Py_InitModule("PyGUI", PyGUI_functions);	/* Add some symbolic constants to the module */	d = PyModule_GetDict(m);	ErrorObject = PyErr_NewException("PyGUI.error", NULL, NULL);	PyDict_SetItemString(d, "error", ErrorObject);	return m;}